mapper.select:
SELECT items.id, items.category, items.item, items.registration_date, items.deleted_date,

orders.id, orders.stocks_id, orders.order_date FROM items


INNER JOIN orders ON items.id = orders.items_id WHERE items.id = #{id}

mapper.update:
UPDATE items INNER JOIN stocks ON items.id = stocks.items_id INNER JOIN orders ON stocks.id = orders.stocks_id
SET stocks.quantity = stocks.quantity + orders.order_date
WHERE items.id = #{id}
